                appendVariant(
                    InterpretationPanel.Variant(
                        gene = "F9",
                        clazz = Clazz.PV,
                        hgvsc = "c.392-2A>T",
                        hgvsp = "p.?",
                        disease = "HB",
                        zygosity = "Het",
                        inheritance = "XL",
                        snv = "hg19:X:138630520:A:T",
                        analysis = "01db4ff4-9689-47b0-b0d9-1659788912c5:22GMD029:029",
                        diseaseFullName = null,
                        interpretation = null,
                        genPhenDb = null
                    )
                )
            }.build()
            , mapOf(
                "F9" to listOf(Disease("Hemophilia B", "HB", listOf("XL")))
            ), "test suffix")
